%script

DECLARE
v_sql varchar2(100);

BEGIN

BEGIN
    v_sql := 'DROP TABLE Wine_TRAIN_DATA PURGE';
    EXECUTE IMMEDIATE v_sql;
    DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
    EXCEPTION
    WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

BEGIN
    EXECUTE IMMEDIATE 'CREATE TABLE Wine_TRAIN_DATA AS (SELECT ID, COUNTRY,DESCRIPTION, DESIGNATION, POINTS_BIN, PRICE, PROVINCE, REGION_1, REGION_2, TASTER_NAME, VARIETY,TITLE
      FROM WineReviews130K_BIN SAMPLE (60) SEED (1))';
    DBMS_OUTPUT.PUT_LINE ('Created Wine_TRAIN_DATA');
END;

BEGIN
    v_sql := 'DROP TABLE Wine_TEST_DATA PURGE';
    EXECUTE IMMEDIATE v_sql;
    DBMS_OUTPUT.PUT_LINE (v_sql ||': succeeded');
    EXCEPTION
    WHEN OTHERS THEN
    DBMS_OUTPUT.PUT_LINE (v_sql ||': drop unneccessary - no table exists');
END;

BEGIN
    EXECUTE IMMEDIATE 'CREATE TABLE Wine_TEST_DATA AS (SELECT ID, COUNTRY,DESCRIPTION, DESIGNATION, POINTS_BIN, PRICE, PROVINCE, REGION_1, REGION_2, TASTER_NAME, VARIETY,TITLE
      FROM WineReviews130K_BIN MINUS SELECT * FROM Wine_TRAIN_DATA)';
    DBMS_OUTPUT.PUT_LINE ('Created Wine_TEST_DATA');
END;

END;
/

