%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('GOOD_WINE_AI');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;
BEGIN
    v_setlst('ALGO_NAME')        := 'ALGO_AI_MDL';
    v_setlst('PREP_AUTO')        := 'ON';
    v_setlst('ODMS_TEXT_POLICY_NAME')        := 'my_policy';
    v_setlst('ODMS_TEXT_MAX_FEATURES')        := '3000';


    DBMS_DATA_MINING.CREATE_MODEL2(
        MODEL_NAME        => 'GOOD_WINE_AI',
        MINING_FUNCTION   => 'ATTRIBUTE_IMPORTANCE',
        DATA_QUERY        => 'select * from WineReviews130KTarget',
        SET_LIST          => v_setlst,
        CASE_ID_COLUMN_NAME => 'ID',
        TARGET_COLUMN_NAME  => 'POINTS_BIN');
END;
/

%script

BEGIN DBMS_DATA_MINING.DROP_MODEL('Wine_CLASS_MODEL_SVM');
EXCEPTION WHEN OTHERS THEN NULL; END;
/
DECLARE
    v_setlst DBMS_DATA_MINING.SETTING_LIST;

BEGIN
    v_setlst('PREP_AUTO') := 'ON';
    v_setlst('ALGO_NAME') := 'ALGO_SUPPORT_VECTOR_MACHINES';
    v_setlst('ODMS_TEXT_POLICY_NAME') := 'my_policy';
    v_setlst('ODMS_TEXT_MAX_FEATURES') := '3000';

    DBMS_DATA_MINING.CREATE_MODEL2(
        'Wine_CLASS_MODEL_SVM',
        'CLASSIFICATION',
        'SELECT * FROM Wine_TRAIN_DATA',
        v_setlst,
        'ID',
        'POINTS_BIN');
END;

%script

-- Drop apply result table
DECLARE
  v_sql varchar2(200);
BEGIN
    v_sql := 'DROP TABLE Wine_APPLY_RESULT PURGE';
    EXECUTE IMMEDIATE v_sql;
    EXCEPTION WHEN OTHERS THEN NULL;
END;
/

DECLARE
  v_sql varchar2(200);
BEGIN
    v_sql := 'DROP TABLE Wine_LIFT_TABLE PURGE';
    EXECUTE IMMEDIATE v_sql;
    EXCEPTION WHEN OTHERS THEN NULL;
END;
/

-- Score the data and compute lift
BEGIN
  DBMS_DATA_MINING.APPLY('Wine_CLASS_MODEL_SVM','Wine_TEST_DATA','ID','Wine_APPLY_RESULT');
  DBMS_DATA_MINING.COMPUTE_LIFT('Wine_APPLY_RESULT','WINE_TEST_DATA','ID','POINTS_BIN',
                                'Wine_LIFT_TABLE','GT_90_Points','PREDICTION','PROBABILITY',100);
END;

%sql

-- Focus on the Wines That Have Been Predicted to be Good Wines (GT_90_Points) vs. Bad Wines (LT_90_Points)

SELECT A.*, B.* FROM Wine_APPLY_RESULT A, Wine_TEST_DATA B WHERE PREDICTION = ${PREDICTION='GT_90_Points','GT_90_Points'|'LT_90_Points'} AND a.ID = b.ID;

